#include <iostream>
#include <string>
#include <vector>
#include <unordered_map>

using namespace std;

class Solution {
public:
    vector<int> findSubstring(string s, vector<string>& words) {

        vector<int> ans;
        int sl = s.size();          // s length
        int wl = words[0].size();   // word's length
        int tl = words.size() * wl; // total length

        int isValid = 1;

        unordered_map<string, int> wordMap, windowMap;
        for(string item : words)    ++wordMap[item];

        for(int i=0; i<=sl-tl; ++i) {
            /*windowMap.clear();
            windowMap.insert(wordMap.begin(), wordMap.end());*/
            windowMap = wordMap;    // copy map
            isValid = 1;
            for(int j=i; j<i+tl; j+=wl) {           // match sub string
                string win_s = s.substr(j, wl);     // window s
                
                --windowMap[win_s];
                if(windowMap[win_s] < 0)   break;
            }
            // check whether valid
            for(auto &item : windowMap) {
                if(item.second != 0) {
                    isValid = 0;
                    break;
                }
            }
            if(isValid)     ans.push_back(i);
        }
        
        return ans;
    }
};

